import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'project';

  LoginForm = false;
  viewForm= true;

  goToLogin(){
    this.viewForm = false;
    this.LoginForm = true;
  }
  firstStep(){
    this.LoginForm = false;
  }
}
