import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'project';

  LoginForm = false;
  viewForm= true;
  LoginForm1 = false;
  LoginForm2 = false;

  goToLogin(){
    this.viewForm = false;
    this.LoginForm = true;
  }
  firstStep(){
    this.LoginForm = false;
    this.LoginForm1 = true;
    this.viewForm = false;
  }
  previous(){
    this.LoginForm1 =false;
    this.LoginForm = true;
  }
  moveTo(){
    this.LoginForm1 =false;
    this.LoginForm2 = true;
    this.viewForm = false;
    this.LoginForm = false;
  }
}
